mapper.select:
select ID, ProcuremenID, orderID, ProductsID, isMainProducts, Demand_Number, placeOrderNumber, notPlaceOrderNumber, Remarks, storageNumber, PRODUCTS_NAME, MEASUREMENT_COMPANY, estimatedPrice from (select S.ID, ProcuremenID, o.order_id as orderID, p.status as approvalStatus, s.ProductsID, isMainProducts, Demand_Number, CASE WHEN o.purchase_number is null then 0 when o.purchase_number is not null then o.purchase_number end as placeOrderNumber, CASE WHEN o.purchase_number is null THEN s.demand_number WHEN o.purchase_number is not null THEN s.demand_number - o.purchase_number END as notPlaceOrderNumber, s.Remarks, sp.storageNumber, name as PRODUCTS_NAME, MEASUREMENT_COMPANY, t.ESTIMATED_PRICE as estimatedPrice from T_Procurement_Demand_DETAIL S LEFT JOIN t_Procurement_Demand_Primary p on s.procuremenid = p.id left join T_SALES_ORDER_PRIMARY o on p.order_id=o.id LEFT JOIN T_PRODUCTS T ON T.ID = S.ProductsID left join ( select procurementid,productsid,sum(purchase_number) purchase_number from t_purchaseorder_detail group by procurementid,productsid) o on s.id = o.procurementid left join ( select procurementid, productsid, sum(purchase_number) storageNumber from t_purchaseorder_detail pd left join t_purchaseorder_primary pp on pd.purchaseorderid = pp.id where pp.status=3 group by procurementid, productsid) sp on s.id = sp.procurementid )
and NOTPLACEORDERNUMBER > #{notPlaceOrderNumber}
and approvalStatus = #{approvalStatus}
and id = #{id}
and ProcuremenID = #{procurementID}
and s.ProductsID = #{productsID}
order by ${orderByClause}

		select count(S.ID)

		from T_Procurement_Demand_DETAIL S
		LEFT JOIN t_Procurement_Demand_Primary p
		on s.procuremenid = p.id
		LEFT JOIN T_PRODUCTS T
		ON T.ID = S.ProductsID
		left join ( select procurementid,productsid,sum(purchase_number)
		purchase_number from t_purchaseorder_detail group by
		procurementid,productsid) o
		on s.id = o.procurementid
		
		
			and NOTPLACEORDERNUMBER = #{notPlaceOrderNumber}
		
		
			and p.status = #{approvalStatus}
		
		
			and id = #{id}
		
		
			and ProcuremenID = #{procurementID}
		
		
			and s.ProductsID = #{productsID}
		
		
			order by ${orderByClause}
