mapper.select:
select pp.id, pp.purchaseorderid, pp.purchasedate, pp.customer_id, pp.status, pd.productsid, pd.purchase_number, pd.price, sd.storage_number, sp.order_no_id, sp.storage_date, sw.name as warehouse_Name, pr.name as products_Name, di.descr as measurement_company, sp.deliverynumber, sd.storage_number*pd.price as order_Value from t_purchaseorder_primary pp left join t_purchaseorder_detail pd on pp.id = pd.purchaseorderid left join t_storage_detail sd on sd.purchaseorderid=pd.id left join t_storage_primary sp on sd.storageprimaryid=sp.id left join t_products pr on pr.id = pd.productsid left join SYS_DICTIONARY di on di.key = pr.measurement_company left join sys_warehouse sw on sw.id = sp.warehouse_id
and pp.customer_id = #{customerID}
and sp.storage_date >= #{startDate}
and sp.storage_date <= #{endDate}
order by ${orderByClause}

select count(pp.id) from t_purchaseorder_primary pp left join t_purchaseorder_detail pd on pp.id = pd.purchaseorderid left join t_storage_detail sd on sd.purchaseorderid=pd.id left join t_storage_primary sp on sd.storageprimaryid=sp.id
and pp.customer_id = #{customerID}
and sp.storage_date >= #{startDate}
and sp.storage_date <= #{endDate}
